    /**
     * {@inheritdoc}
     */
    public function safeUp()
    {
        foreach ($this->services as $name => $code_1c) {
            $service = \core\models\division\DivisionService::find()->company(214)
                ->andWhere(['lower(service_name)' => mb_strtolower($name)])
                ->one();
            if (!$service) {
                echo "Service '$name' NOT FOUND ---" . PHP_EOL;
                continue;
            }
            $service->updateAttributes(['code_1c' => $code_1c]);
        }

        echo "----------------------------------------------------" . PHP_EOL;

        foreach ($this->staff as $full_name => $code_1c) {
            $matches = preg_split("/\s+/", $full_name);

            $name = $matches[0] ?? null;
            $surname = $matches[1] ?? null;

            $model = \core\models\Staff::find()->company(false, 214)
                ->andFilterWhere(['{{%staffs}}.name' => $name, 'surname' => $surname])
                ->orFilterWhere(['{{%staffs}}.name' => $surname, 'surname' => $name])
                ->one();

            if (!$model) {
                echo "Staff '$full_name' NOT FOUND ---" . PHP_EOL;
                continue;
            }
            $model->updateAttributes(['code_1c' => $code_1c]);
        }

    }

    /**
     * {@inheritdoc}
     */
    public function safeDown()
    {
        $this->update(\core\models\division\DivisionService::tableName(), ['code_1c' => null]);
        $this->update(\core\models\Staff::tableName(), ['code_1c' => null]);
    }
